package week1.day2;

public class LearnLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 5;
		
		while (x < 10) {
			System.out.println(x);
			x = x+1;
		}
		
		//x = 15;
		
		do {
			System.out.println(x);
		}while( x <10);

	}

}
